    {
      title: 'React',
      url: 'https://reactjs.org/',
      author: 'Jordan Walker',
      points: 4,
      objectID: 0,
    },
    {
      title: 'Redux',
      url: 'https://redux.js.org/',
      author: 'Jordan Walker',
      points: 5,
      objectID: 1,
    }
  ]
  const [searchTerm, setSearchTerm] = useState('');

  const labelForSearch = 'Search';

  const handleSubmit = (e) => {
