import React, { useState } from 'react';

function App() {

 const [tasks, setTasks] = useState([
   { id: 1, title: 'Task 1', explanation: 'This is task 1' },
   { id: 2, title: 'Task 2', explanation: 'This is task 2'},
   { id: 3, title: 'Task 3', explanation: 'This is task 3'}
 ]);

  return (
    <>
      <h1>Task List</h1>
      <hr/>
      <ul>
          {
            tasks.map(
              (task) => (
                <li key={task.id}>
                  <span>{task.title}</span>
                  <p>{task.explanation}</p>
                  <button onClick={() => setTasks(tasks.filter(t => t.id !== task.id))}>
                    Delete
                  </button>
                </li>
              )
            )
          }
      </ul>
    </>
  )

}

export default App;
