import React, { useEffect, useRef } from "react";

const SplineSplash = () => {
  const containerRef = useRef(null);

  useEffect(() => {
    const script = document.createElement("script");
    script.type = "module";
    script.src = "https://unpkg.com/@splinetool/viewer@1.10.13/build/spline-viewer.js";
    document.body.appendChild(script);

    const viewer = document.createElement("spline-viewer");
    viewer.setAttribute("url", "https://prod.spline.design/abc123xyz/scene.splinecode"); // use your embed link here
    viewer.style.width = "100%";
    viewer.style.height = "100%";
    viewer.style.border = "none";

    containerRef.current.appendChild(viewer);

    return () => {
      containerRef.current.innerHTML = "";
      document.body.removeChild(script);
    };
  }, []);

  return (
    <div style={{ position: "relative", width: "100vw", height: "100vh" }}>
      <div ref={containerRef} style={{ width: "100%", height: "100%" }} />
      <div
        style={{
          position: "absolute",
          top: "40%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          color: "white",
          fontSize: "3rem",
          fontWeight: "bold",
          textShadow: "0 0 10px rgba(0,0,0,0.7)",
          textAlign: "center",
          pointerEvents: "none",
        }}
      >
        Welcome to NexBot
      </div>
    </div>
  );
};

export default SplineSplash;
