import React, { useState } from 'react';

function App() {

  const list = [
    {
      title: 'React',
      url: 'https://reactjs.org/',
      author: 'Jordan Walker',
      points: 4,
      objectID: 0,
    },
    {
      title: 'Redux',
      url: 'https://redux.js.org/',
      author: 'Jordan Walker',
      points: 5,
      objectID: 1,
    }
  ]
  const [searchTerm, setSearchTerm] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, stateError] = useState({
    errorPassword: '',
    errorConfirmPassword: ''
  });

  const handleFromSubmit = (e) => {
    e.preventDefault();
    console.log(password, confirmPassword);
    if(password === '') {
      stateError(prevState => ({ ...prevState, errorPassword: true }));
    }
    if(confirmPassword === '') {
      stateError(prevState => ({ ...prevState, errorConfirmPassword: true }));
    }
    if(password && confirmPassword) {
      if(password === confirmPassword) {
        alert('Form submitted successfully!');
        // Reset form
        setPassword('');
        setConfirmPassword('');
        stateError({ errorPassword: '', errorConfirmPassword: '' });
      } else {
        alert('Passwords do not match!');
      }
    }
    if(password === '' && confirmPassword === '') {
      stateError({ errorPassword: true, errorConfirmPassword: true });
    }

  }



  const filteredData = list.filter(item =>
    item.title.toLowerCase().includes(searchTerm.toLowerCase())
  );
  return (
    <>

    <CustomInput
      id='search'
      type='text'
      value={searchTerm}
      onChange={e => setSearchTerm(e.target.value)}
    />


     <PasswordInput />

    <hr/>


    <ul>
    {filteredData && filteredData.map(
      (item, index) => (
        <li key={item.objectID}>
          <span>
            {item.title}
          </span>
        </li>
      )
    )}
    </ul>

  <hr/>

  <form onSubmit={handleFromSubmit}>

    <label htmlFor='password'>Password</label>
    <input
      id='password'
      type='password'
      value={password}
      onChange={e => setPassword(e.target.value)}
      placeholder='Enter your password'
      style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
      />
    {error.errorPassword && <p style={{ color: 'red'}} >{'Please, provide the Password'}</p>}

    <label htmlForm='confirmPassword'>Confirm Password</label>
    <input 
      id='confirmPassword'
      type='password'
      value={confirmPassword}
      onChange={e => setConfirmPassword(e.target.value)}
      placeholder='Confirm your password'
      style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
    />
    {error.errorConfirmPassword && <p style={{color: 'red'}}> {"Please, provide the confirm Password"}</p>}

    <button type="submit" style={{ padding: '10px 20px', backgroundColor: '#007bff', color: '#fff', border: 'none', borderRadius: '4px' }}>
      Submit
    </button>

  </form>





    </>
  );
}

export default App;


const CustomInput = ({ id, type, value, onChange}) => {
  return (

    <>
    <input 
      id={id}
      type={type}
      value={value}
      onChange={onChange}
      placeholder='Search...'
      style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
    />
    </>
  );
}

const PasswordInput = () => {

}
