import React, { useState } from 'react';


function App() {
    const [formState, setFormState] = useState({
      firstName: "",
      lastName: "",
      emailAddress: "",
      password: "",
      role: ""
    });

    const [passwordError, setPasswordError] = useState("");

    const handleChange = (e) => {
      const { name, value } = e.target;

      setFormState((prevState) => ({
        ...prevState,
        [name]: value
      }));

    }

    const handleSubmit = (e) => {

      e.preventDefault();

      if (formState.password.length < 8) {
        setPasswordError("Password should have at least 8 characters");
      }else{
        setPasswordError("");
        console.log("Form submited", formState);
      }

      console.log(formState);

    }




    return (
      <>
        <form onSubmin={handleSubmit}>

          <fieldset>
            <div>
              <label htmlFor='firstName'>
              FirsName
              </label>
              <input 
                type="text" 
                name="firstName"
                placeholder='First Name'
                value={formState.firstName}
                onChange={handleChange}
              />
            </div>


            <label htmlFor='lastName'>
              Last name
            </label>
            <input 
              type="text" 
              placeholder='Last name'
              name="lastName"
              value={formState.lastName}
              onChange={handleChange}
            />

            <label htmlFor='emailAddress'>
              Email address
            </label>
            <input 
              type="text" 
              placeholder='Email address'
              name="emailAddress"
              value={formState.emailAddress}
              onChange={handleChange}
            ></input>

            <label htmlFor='password'>
              Email address
            </label>
            <input 
              type="text" 
              placeholder='Password'
              name="password"
              value={formState.password}
              onChange={handleChange}
            ></input>
            {passwordError && <p style={{ color: "red" }}> Password should have at least 8 characters</p>}

            <label htmlFor="role">
              Role
            </label>
            <select value={formState.role} onChange={handleChange}>
              <option value="">--Please choose an option--</option>
              <option value="chocolate">Chocolate</option>
              <option value="vanilla">Vanilla</option>
              <option value="strawberry">Strawberry</option>
            </select>
             {formState.role && <p>You selected: {formState.role}</p>}
            <button type='submit'>Submit</button>

          </fieldset>
        </form>


      </>
    )


}



export default App;
