// src/components/SplineSplash.js
import React from "react";

const SplineSplash = () => {
  return (
    <div
      style={{
        position: "relative",
        width: "100vw",
        height: "100vh",
        backgroundColor: "#000",
        overflow: "hidden",
      }}
    >
      <iframe
        src="https://my.spline.design/particles-hBSBgx72ycnJvr2VDaECr0cG/"
        frameBorder="0"
        width="100%"
        height="100%"
        title="Spline Scene"
        style={{ position: "absolute", top: 0, left: 0, zIndex: 0 }}
      ></iframe>

      {/* Dark overlay */}
      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundColor: "rgba(0, 0, 0, 0.5)",
          zIndex: 1,
          pointerEvents: "none",
        }}
      ></div>

      {/* Shining Text */}
      <div
        style={{
          position: "absolute",
          top: "40%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          fontSize: "3rem",
          fontWeight: "bold",
          textAlign: "center",
          pointerEvents: "none",
          zIndex: 2,

          // Shiny effect:
          background: "linear-gradient(90deg, #fff, #f0f0f0, #fff)",
          WebkitBackgroundClip: "text",
          WebkitTextFillColor: "transparent",
          animation: "shine 2s infinite linear",
          textShadow: "0 0 10px rgba(255,255,255,0.5)",
        }}
      >
        Welcome to NexBot
      </div>

      {/* Define the keyframes inside a <style> tag */}
      <style>
        {`
          @keyframes shine {
            0% {
              background-position: -200%;
            }
            100% {
              background-position: 200%;
            }
          }

          div {
            background-size: 200% auto;
          }
        `}
      </style>
    </div>
  );
};

export default SplineSplash;
