import React, { useState } from 'react';


function App() {

  const [todos, setTodos] = useState([
    {
    id: 1,
    title: 'Chocolate Cake',
    description: ''

  },
  {
    id: 2,
    title: 'Vanilla Ice Cream',
    description: ''

  }
  ])

  const reverseOrder = () => {

    setTodos([...todos].reverse());
  }

  const updateDescription = (id, newDes) => {
    const updated = todos.map( todo => 
      todo.id === id ? {...todo, descripton: newDes} : todo
    );
    setTodos(updated);
  }

  return (
    <>
    <button onClick={reverseOrder}>reverse</button>

    <table>
      <tbody>
        {todos.map(
          (todo, index) => 
          (
            <ToDo key={index} id={todo.id} createdAt={todo.title} description={description} onDescriptionChange={updateDescription}/> 
          )


        )}
      </tbody>
    </table>
    </>

  )
}

export default App;


const ToDo = ({ key, id, createdAt, description, onDescriptionChange })=>{
  return (
    <tr>
      <td>
        <label>
          {id}
        </label>
        <input 
          type="text"
          value={description}
          onChange={(e) => onDescriptionChange(id, e.target.value)}
        />

      </td>
      <td>
        <label>
          {createdAt}
        </label>
      </td>
    </tr>
  )
}
