import React, { useState } from 'react';

function App() {

  const list = [
    {
      title: 'React',
      url: 'https://reactjs.org/',
      author: 'Jordan Walker',
      points: 4,
      objectID: 0,
    },
    {
      title: 'Redux',
      url: 'https://redux.js.org/',
      author: 'Jordan Walker',
      points: 5,
      objectID: 1,
    }
  ]

  const [searchTerm, setSearchTerm] = useState('');

  function getTitle(title) {
    return title ? title: "World";
  }

  const handleSearch = (event) => {
    console.log(event.target.value);
    setSearchTerm(event.target.value);

  }


  const filteredList = list.filter(listItem => listItem.title.toLowerCase().includes(searchTerm.toLocaleLowerCase()));
  return (
    <div className="App">
      <h3>Hello, {getTitle()}</h3>

      <hr/>
      <Search handleSearch={handleSearch}/>
      <hr/>
      <List list={filteredList}/>
    </div>
  );
}

export default App;


const List = ({ list }) => {
  return (
         <ul>
        {list.map(function (item){
          return (
            <Item key={item.objectID} item={item} />
          )
        })}
      </ul>
  )
}

const Search = (props) => {



   const handleSearchChange = (event) => {

    props.handleSearch(event);
   } 


  return (
   <>
      <label htmlFor="search">Search: </label>
      <input id="search" type="text" placeholder="Search..." onChange={(e) => handleSearchChange(e)}/>
   </>
  )
}

const Item = (props) => {
  return (
    <li key={props.item.objectID}>
              <span>
                <a href={props.item.url}>{props.item.title}</a>
              </span>
              <span>
                {props.item.author}
              </span>
              <span>
                {props.item.num_comments}
              </span>
              {props.item.points
              }
            </li>
  )
}
