// SplineSplash.js
import React from "react";

const SplineSplash = () => {
  return (
    <div
      style={{
        position: "relative",
        width: "100vw",
        height: "100vh",
        backgroundColor: "#000", // fallback background
        overflow: "hidden",
      }}
    >
      <iframe
        src="https://my.spline.design/particles-hBSBgx72ycnJvr2VDaECr0cG/"
        frameBorder="0"
        width="100%"
        height="100%"
        title="Spline Scene"
        style={{ position: "absolute", top: 0, left: 0, zIndex: 0 }}
      ></iframe>

      {/* Dark overlay */}
      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundColor: "rgba(0, 0, 0, 0.5)",
          zIndex: 1,
          pointerEvents: "none",
        }}
      ></div>

      {/* Overlay text */}
      <div
        style={{
          position: "absolute",
          top: "40%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          color: "white",
          fontSize: "3rem",
          fontWeight: "bold",
          textAlign: "center",
          textShadow: "0 0 10px rgba(0,0,0,0.7)",
          pointerEvents: "none",
          zIndex: 2,
        }}
      >
        Welcome to My App
      </div>
    </div>
  );
};

export default SplineSplash;
