import React, { useState } from 'react';

let topDesserts = [
  {
    id: 1,
    title: 'Chocolate Cake',
    description: 'A rich and moist chocolate cake topped with creamy chocolate frosting.',
    image: 'https://example.com/chocolate-cake.jpg',
    price: 4.99,
  },
  {
    id: 2,
    title: 'Vanilla Ice Cream',
    description: 'Creamy vanilla ice cream made with real vanilla beans.',
    image: 'https://example.com/vanilla-ice-cream.jpg',
    price: 2.99,
  },
  {
    id: 3,
    title: 'Apple Pie',
    description: 'Classic apple pie with a flaky crust and sweet apple filling.',
    image: 'https://example.com/apple-pie.jpg',
    price: 3.99,
  },
  {
    id: 4,
    title: 'Cheesecake',
    description: 'Rich and creamy cheesecake with a graham cracker crust.',
    image: 'https://example.com/cheesecake.jpg',
    price: 5.49,
  },
  {
    id: 5,
    title: 'Brownies',
    description: 'Fudgy brownies with a crackly top and chewy center.',
    image: 'https://example.com/brownies.jpg',
    price: 2.49,
  },
];

function App() {

  const listItems = topDesserts.map(
    dessert => {
      return (
        <div key={dessert.id}>
          <h2>{dessert.title}</h2>
          <p>{dessert.description}</p>
          <img src={dessert.image} alt={dessert.title} style={{ width: '200px', height: 'auto' }} />
          <p>Price: ${dessert.price.toFixed(2)}</p>
          <button>Add to Cart</button>
        </div>
      )

    }
  );


  return (
    <>
    <ul>
      {listItems}
    </ul>

    </>
  )

}

export default App;
